
function(_,_,combatEvent,_,sourceGUID,_,_,_,_,_,_,_,spellID,_,_,amount)
    if(sourceGUID == UnitGUID("player") and combatEvent == "SPELL_DAMAGE") then
        WA_Tenebrus_max = WA_Tenebrus_max or 0;
        if(amount>WA_Tenebrus_max) then
            WA_Tenebrus_max = amount
        end
        return true;
    end
end

-- Reset
function(event)
    if(event == "PLAYER_REGEN_ENABLED") then
        WA_Tenebrus_max = 0;
        return true;
    end   
end

--Trigger
function(_,_,combatEvent,_,sourceGUID,_,_,_,_,_,_,_,spellID,_,_,amount)
    
    if(sourceGUID == UnitGUID("player") and combatEvent == "SPELL_DAMAGE") then      
        return true;
    end
end

--Untrigger
function(event)
    if(event == "PLAYER_REGEN_ENABLED") then
        return true;
    end   
end

-- Display

function()
    local dmg = WA_Tenebrus_MAX or 0;
    return string.format("Hardest Hit\n|cffffff00Damage:|r%.2f", dmg/1000000) .. "m";
end

